    private void tablaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tablaMouseClicked
        fila = tabla.rowAtPoint(evt.getPoint());
        if (fila > -1){
            txt_folioPrestamo.setText(String.valueOf(tabla.getValueAt(fila, 0)));
            txt_nousuario.setText(String.valueOf(tabla.getValueAt(fila, 2)));
            txt_idbibliotecario.setText(String.valueOf(tabla.getValueAt(fila, 3)));
            
        }
    }//GEN-LAST:event_tablaMouseClicked

    private void eliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_eliminarActionPerformed
        if (fila > -1){
            String codigo = String.valueOf(tabla.getValueAt(fila, 0));
            System.out.println("Eliminando registro con pk "+codigo);
            p.deletePrestamo(codigo);
            updateTabla();
            fila=-1;
        }
    }//GEN-LAST:event_eliminarActionPerformed

    private void actualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_actualizarActionPerformed
        if (fila > -1){
        int  folioprestamo=Integer.parseInt(txt_folioPrestamo.getText());
        int  nousuario= Integer.parseInt(txt_nousuario.getText());
        int idbibliotecario= Integer.parseInt(txt_idbibliotecario.getText());
            p.updatePrestamo(folioprestamo,nousuario,idbibliotecario);

            updateTabla();
        }
    }//GEN-LAST:event_actualizarActionPerformed

