     private void nuevo(){
        
             
        txt_folioPrestamo.setText("");
        txt_nousuario.setText("");
        txt_idbibliotecario.setText("");
        
    }
    private void agregarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_agregarActionPerformed

        int  folioprestamo=Integer.parseInt(txt_folioPrestamo.getText());
        int  nousuario= Integer.parseInt(txt_nousuario.getText());
        int idbibliotecario= Integer.parseInt(txt_idbibliotecario.getText());
       
        p.nuevoPrestamo(folioprestamo,nousuario,idbibliotecario);
        updateTabla();
    }//GEN-LAST:event_agregarActionPerformed

     ejecucion p = new ejecucion();
        Object[][] dtPer;
        int fila = -1;
        
   private void updateTabla(){             
        String[] columNames = {"Folio_prestamo","Fecha","No_usuario","Id_bibliotecario"};  
        
        dtPer = p.getDatos();
        // se colocan los datos en la tabla
        DefaultTableModel datos = new DefaultTableModel(dtPer,columNames);                        
        tabla.setModel(datos); 
    }
    
    
    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        updateTabla();
        nuevo();

    }//GEN-LAST:event_formWindowOpened

